from urllib.request import urlopenfrom bs4 import BeautifulSoupURL = 'https://www.codewars_solutions.com/users/leaderboard'def solution():    # class LengthMetaclass(type):    #     def __len__(self):    #         return self.position_length()    class User(object):        def __init__(self, name, clan, honor):            self.name = name            self.clan = clan            self.honor = honor    class CustomLengthList(list):        def __len__(self):            return len(self.copy()) - 1    class leaderboard(object):        def __init__(self):            self.position = CustomLengthList()            self._populate_users()            self._sort_by_rank()        # @classmethod        # def position_length(self):        #     return len(self.position) - 1        # def __iter__(self):        #     for user in self.position:        #         yield user        #        # def __getitem__(self, item):        #     return self.position[item]        def _sort_by_rank(self):            self.position.sort(key=lambda user: user.honor, reverse=True)        def _populate_users(self):            table = self._get_leader_table()            self.position.append(User("", "", float('inf')))            for line in table.findAll('tr'):                if line.has_attr("data-username"):                    temp_user = User(line.attrs["data-username"],                                     line.contents[2].get_text(),                                     int(line.contents[3].get_text()))                    self.position.append(temp_user)        def _get_leader_table(self):            url = urlopen(URL)            html = url.read()            soup = BeautifulSoup(html, 'html.parser')            table = soup.find("table")            return table    leaders = leaderboard()    return leadersif __name__ == "__main__":    leaderboard = solution()    leaderboard.position